<script>
jQuery(document).ready( function ($) {
    $(document).on("click", "#selectLocker", function() {
        $('.sameday_open_package_label').css({ 'display': 'none' });

        lockerValue = $(this).val();
        $(this).closest(".radio").children()[0].childNodes[1].checked = false;

        if (lockerValue !== "") {
            $(this).closest(".radio").children()[0].childNodes[1].checked = true;
        }

        $(this).closest(".radio").children()[0].childNodes[1].value = lockerValue;
    });

    $(document).on('click', 'input[name="shipping_method"]', function () {
        $('.sameday_open_package_label').css({ 'display': 'none' });

        let serviceId = $(this).data('serviceid');
        if (serviceId !== null) {
            let openPackage = $('#sameday_open_package_' + serviceId);
            openPackage.css({ 'display': 'block' });
        }
    });

    $(document).on('click', '.sameday_open_package', function () {
        let code = $(this).data('code');
        let isOpcg = code + '.isOpcg';

        $(this).closest(".radio").children()[0].childNodes[1].value = code;
        if ($(this).is(':checked')) {
            $(this).closest(".radio").children()[0].childNodes[1].value = isOpcg;
        }
    });
});
</script>
